class LogoParticle {
  constructor(x, y, hues) {
    this.spawn(x, y, hues);
  }
  
  spawn(x, y, hues) {
    let r = 300 + Math.random() * 500;
    let theta = Math.random() * Math.PI * 2;
    this.x = r * Math.cos(theta);
    this.y = r * Math.sin(theta);
    this.to_x = x;
    this.to_y = y;
    this.vx = 0;
    this.vy = 0;
    this.r = 0;
    this.vr = 0;
    this.to_r = 5 + Math.random() * 10;
    this.speed = 0.01 + Math.random() * 0.09;
    this.to_hue = hues[0] + (hues[1] - hues[0]) * Math.random();
    this.hue = Math.random() * 360;
    this.trail = buildRandomCurve([this.x, this.y], [x, y], 4 + Math.random()*3 | 0);
    this.frame_count = 0;
  }
  
  update() {
    if(this.frame_count < this.trail.length) {
      this.to_x = this.trail[this.frame_count][0];
      this.to_y = this.trail[this.frame_count][1];
    } 
    this.vx += (this.to_x - this.x) * this.speed;
    this.vy += (this.to_y - this.y) * this.speed;
    this.x += this.vx * this.speed;
    this.y += this.vy * this.speed;
    this.vx *= 0.925;
    this.vy *= 0.925;
    this.vr += (this.to_r - this.r) * this.speed;
    this.r += this.vr * this.speed;
    this.r = Math.abs(this.r) < 0.1 ? 0 : this.r;
    this.vr *= 0.99;
    this.hue += (this.to_hue - this.hue) * 0.005;
    this.frame_count += 1;
  }
}

